import React from 'react';
import './App.css';
import ListItems from './ListItems'
import ReactCalendar from "./Calander"
import { library } from '@fortawesome/fontawesome-svg-core'
import { faTrash } from '@fortawesome/free-solid-svg-icons'

library.add(faTrash)

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      currentItem: {
        text: '',
        key: ''
      }
    }
    this.addItem = this.addItem.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
    this.setUpdate = this.setUpdate.bind(this);
  }
  addItem(e) {
    e.preventDefault();
    const newItem = this.state.currentItem;
    if (newItem.text !== "") {
      const items = [...this.state.items, newItem];
      this.setState({
        items: items,
        currentItem: {
          text: '',
          key: ''
        }
      })
    }
  }
  handleInput(e) {
    this.setState({
      currentItem: {
        text: e.target.value,
        key: Date.now()
      }
    })
  }
  deleteItem(key) {
    const filteredItems = this.state.items.filter(item =>
      item.key !== key);
    this.setState({
      items: filteredItems
    })

  }
  setUpdate(text, key) {
    console.log("items:" + this.state.items);
    const items = this.state.items;
    items.map(item => {
      if (item.key === key) {
        console.log(item.key + "    " + key)
        item.text = text;
      }
    })
    this.setState({
      items: items
    })


  }
  render() {
    return (
      <div className="App">
        <h1 className="headding">To <span id="do">Do</span> List</h1>

        <header>
          <form id="to-do-form" onSubmit={this.addItem}>
            <input className="input" type="text" placeholder="Enter Task Here" value={this.state.currentItem.text} onChange={this.handleInput}></input>

            <button id="btn" type="submit">Add New Task</button>

          </form>
          <p>{this.state.items.text}</p>
          <p>{this.state.currentitems.key}</p>

          <ListItems items={this.state.items} deleteItem={this.deleteItem} setUpdate={this.setUpdate} />

        </header>
      </div>
    );
  }
}


export default App;
