import React from 'react';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state={
      items:[],
      currentItem:{
        text:"",
        key:"",
      }


    }

  }
handleInput(e){
  this.setState({
    currentItem:{
      text:e.target.value,
      key:Date.now()
    }
  })
}
addItem(e){
  e.preventDefault();
  const newItem = this.state.currentItem;
  if(newItem.text !==""){
    const items = [...this.state.items, newItem];
  this.setState({
    items: items,
    currentItem:{
      text:'',
      key:''
    }
  })
  }
}
deleteItem(key){
  const filteredItems= this.state.items.filter(item =>
    item.key!==key);
  this.setState({
    items: filteredItems
  })

}
setUpdate(text,key){
  console.log("items:"+this.state.items);
  const items = this.state.items;
  items.map(item=>{      
    if(item.key===key){
      console.log(item.key +"    "+key)
      item.text= text;
    }
  })
  this.setState({
    items: items
  })


}



  render(){
    return(
      <div className="App">
      <header>
        <h1 className="headding">ToDo list</h1>
        <form id="to-do-form" onSubmit={this.addItem}>
        <input className="input" type="text" placeholder="Enter Text Here" value={this.state.currentItem.text}
        onChange={this.handleInput}></input>
        <button id="btn" type="submit" >Add Item to List</button>
        </form>
      </header>
      </div>

    )
  }
}

export default App;
