import React from 'react';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state={
      items:[],
      currentItem:{
        text:"",
        key:"",
      }


    }

  }
handleInput(e){
  this.setState({
    currentItem:{
      text:e.target.value,
      key:Date.now
    }
  })
}



  render(){
    return(
      <div className="App">
      <header>
        <h1 className="headding">ToDo list</h1>
        <form id="to-do-form" onSubmit={this.addItem}>
        <input className="input" type="text" placeholder="Enter Text Here" value={this.state.currentItem.text}
        onChange={this.handleInput}></input>
        <button id="btn" type="submit" >Add Item to List</button>
        </form>
      </header>
      </div>

    )
  }
}

export default App;
